package prjDay3;

public class Food {
	String name;
	int price;
	public Food() {
		super();
		// TODO Auto-generated constructor stub
	}
	
	public Food(String name, int price) {
		super();
		this.name = name;
		this.price = price;
	}
	
	public String getName() {
		return name;
	}
	public int getPrice() {
		return price;
	}
	
	
}
